mapper.select:
select * from tbl_member
where email = #{email}
limit 1

mapper.update:
update tbl_member set
phone = #{phone},
addr = #{addr},
email = #{email}
where id = #{id}

update tbl_member set
mUuid = #{mUuid},
mOrigin =
#{mOrigin},
mPath = #{mPath}
where id = #{id}

mapper.select:
select count(*) from
tbl_member
where id = #{id}

select count(*) from
tbl_member
where email = #{email}
